<script>
import DashboardPerimeter from '~/authorizations/DashboardPerimeter'
import _ from 'lodash'
export default {
  name: 'IntelligencesMultiples',
  layout: 'activity',
  routePerimeter: DashboardPerimeter,
  routePerimeterAction: 'accessHighSchoolStudentDashboard',
  async asyncData( { app, route, error, store } ) {
    try {
      const chapters = store.state.auth.user.course.chapters
      let domain = _.find(chapters, { slug: 'activity-area' })
      let introspection = _.find(domain.children, { slug: 'introspection' })

      if(!_.some(introspection.activities, {'id': '52485617-002b-4863-9e58-721be716f399'})) {
        return error({ statusCode: 404, message: 'Post not found' })
      }

      let response
      const version_id = _.get(_.find(introspection.activities, {id: '52485617-002b-4863-9e58-721be716f399'}), 'version_id', null)

      if (version_id) {
        response = await app.$api.activities.getVersionsByIds([version_id])
      } else {
        response = await app.$api.activities.getVersions({
          parent_ids: ['52485617-002b-4863-9e58-721be716f399'],
          isDefault: true
        })
      }
      const version = response.data[0] ||Â {}

      response = await app.$api.activities.getSelections({version_ids: [version.id]})
      const selections = response.data || []

      const selections_ids = selections.map(selection => selection.id)

      response = await app.$api.activities.getChoices(selections_ids)
      const personnalites = response.data || []

      personnalites.forEach(function(personnalite) {
        personnalite.like = false
        personnalite.value = false
      })

      response = await app.$api.activities.getUserTokens(app.store.state.auth.user.userID, '52485617-002b-4863-9e58-721be716f399')
      const tokens = response.data ||Â []

      response = await app.$api.activities.getUserLogs(app.store.state.auth.user.userID, '52485617-002b-4863-9e58-721be716f399')
      const activityLogs = response.data[0]

      if (activityLogs) {
        return { personnalites, tokens, activityLogs }
      } else {
        return { personnalites, tokens }
      }
    } catch ( e ) {
      return 'error'
    }
  },
  data() {
    return {
      activity_id: '52485617-002b-4863-9e58-721be716f399',
      step: 0,
      personnalites: [],
      tokens: [],
      personnalitesStep: 1,
      results: [],
      activityLogs: {
        status_name: 'open',
        activity_id: '52485617-002b-4863-9e58-721be716f399',
        config: {}
      },
      endResults: null
    }
  },
  mounted() {
    this.personnalitesStep = 1
  },
  methods: {
    startActivity: function () {
      this.step = 1
      this.$router.push( '/dashboard/student/domaines/introspection/traits-de-personnalite/etape-1' )
    },
    stopActivity: function () {
      this.step = 0
    },
    setStep: function ( step ) {
      this.step = step
    },
    getResults: function ( results ) {
      this.results = results
    },
    getEndResults: function ( results ) {
      this.endResults = results
    },
    setActivityLogs: function ( activityLogs ) {
      this.activityLogs = activityLogs
    },
    createLogs: async function (callback) {
      //SENDING LOGS
      this.activityLogs.user_id = this.$store.state.auth.user.userID
      await this.$api.activities.createLog(this.activityLogs)
      callback()
    },
    createResults: function () {
      //SENDING RESULTS
      // this.$api.activities.createResults(this.$store.state.auth.user.userID, this.endResults)
    }
  }
}
</script>

